import React from 'react';
import logo from './logo.svg';
import './App.css';
import { userInfo } from 'os';
interface NameProps {

}

interface NameState {
  name: string
}

class NameInput extends React.Component<NameProps, NameState> {
  constructor(props: any) {
    super(props);
    this.state = {
      name: ''
    }
    this.changeField = this.changeField.bind(this);
  }
  changeField(event: any) {
    this.setState({name: event.target.value})
  }
  render() {
    return (<div>
      <input type="text" value= {this.state.name} onChange={this.changeField}/>
    </div>)
  }
}

class App extends React.Component {
  render() {
    return(
      <div>
        <NameInput />
        <NameInput />
      </div>
    )
  }
}


export default App;
