import React from 'react';
import logo from './logo.svg';
import './App.css';
import { userInfo } from 'os';
interface WelcomeProps {
  onClick(event: any): void
}

class Welcome extends React.Component<WelcomeProps> {
  constructor(props: any) {
    super(props);
  }
  render() {
    return (
      <button onClick={this.props.onClick}>Welcome</button>
    )
  }
}

class ComeBack extends React.Component<WelcomeProps> {
  constructor(props: any) {
    super(props);
  }
  render() {
    return (
      <button onClick={this.props.onClick}>ComeBack</button>
    )
  }
}

interface IProps {
}

interface IState {
  value: string;
  check: boolean;
}

class App extends React.Component<IProps, IState> {
  constructor(props: any) {
    super(props);
    this.state = {
        value: '',
        check: true
    }
    this.changeValue = this.changeValue.bind(this);
    this.changeCheck = this.changeCheck.bind(this);
  }
  changeValue(event: any) {
    this.setState({
      value: event.target.value
    })
  }
  changeCheck(event: any) {
    this.setState(state=>({
      check: !state.check,
    }))
  }
  render() {
    let check: any;
    let renderPart: any;
    if (check)  renderPart = <Welcome onClick={this.changeCheck} />;
    else renderPart = <ComeBack onClick={this.changeCheck} />
    return (
    <div>
      <div>
        Name:<input type="text" value={this.state.value} onChange={this.changeValue}/>
      </div>
      <div>
        Name2:<input type="text" value={this.state.value} onChange={this.changeValue}/>
      </div>
      <div>
        {renderPart}
      </div>
    </div>
  );
    }
}

export default App;
