import React, { useState, FC, useCallback, useEffect, useReducer } from "react";
// import logo from './logo.svg';
// import "../App.css";
import { setServers } from "dns";
// import { userInfo } from 'os';
// import { stringify } from 'querystring';
import {observable, computed, reaction, action} from 'mobx';  

class Users {
    @observable arr: any[] = [ 
            {id: 1, name: 'Tanya', surname: 'Baikova', age: 18}, 
            {id: 2, name: 'Pasha', surname: 'Sukhov',  age: 16}
        ];
    @observable edit: boolean = false;
    @observable currentEditUser = {name: '', surname: '', age: 0}
    currentEditField(user: any) {
        this.edit = true;
        this.currentEditUser = user;
    }
    deleteUser(id: number): void {
        this.arr = this.arr.filter(el=> el.id !== id);
    }
    editUser(user: any): void{
        this.arr = this.arr.map((el => el.name===user.name ? el=user : el))
        console.log(this.arr);
    }
    addUser(user: any): void {
        this.arr.push(user);
        console.log(this.arr, user)
    };
}
const users = new Users();
export default users;
export {Users}
