import React, { useState, FC, useCallback, useEffect, useReducer } from "react";
// import logo from './logo.svg';
import "./App.css";
import users from './stores/menu-store';
import { Users } from './stores/menu-store';
import { setServers } from "dns";
import {observer} from 'mobx-react-lite';
import {observable} from 'mobx';
// import { userInfo } from 'os';
// import { stringify } from 'querystring';

const AddUser: FC<{addUser: Users['addUser'], arr: Users['arr']}> = ({addUser, arr}) => {
  const [user, stateUser] = useState({name: '', surname: '', age: 0});
  function inputChange(e: any) {
    const {name, value} = e.target;
    stateUser({...user, [name]: value});
  }
  function handleInput() {
    users.addUser(user)
  }
  return (<>
    Name:<input value={user.name} name="name" onChange={inputChange}/>
    Surname:<input value={user.surname} name="surname" onChange={inputChange}/>
    Age:<input value={user.age} name="age" onChange={inputChange}/>
    <button onClick={handleInput}>Add User</button></>
  )
}

const App = observer(() => {
  let users1 = users.arr.map((el: any)=><div className="row">
    <div className="col">{el.name}</div>
    <div className="col">{el.surname}</div>
    <div className="col">{el.age}</div>
    <button onClick={()=>users.deleteUser(el.name)}>Delete</button>
    <button onClick={()=>users.editUser(el.name)}>Edit</button>
  </div>)
  return (<>
  <div>
    <h2>AddUser</h2>
    <AddUser addUser={users.addUser} arr={users.arr}/>
  </div>
  {users.edit ? (<div>
    <h2>Edt User</h2>
    Name:<input/>
    Surname:<input/>
    Age:<input/>
  </div>) : (<div>Bye</div>)}
  <div>
    <h2>View Users</h2>
    {users1}
  </div>
  </>)
}
)
export default App;
