import React, { useState, FC, useCallback, useEffect, useReducer } from "react";
// import logo from './logo.svg';
import "./App.css";
import { setServers } from "dns";
// import { userInfo } from 'os';
// import { stringify } from 'querystring';


const AddUserForm: FC<{addUser(user: any): any}> = ({addUser}) => {
  const initialState = {
    id: 0,
    name: '',
    surname: '',
    age: 0
  }
  const [user, setUser] = useState(initialState);

  function handleChange(event: any) {

    const {name, value} = event.currentTarget;
    setUser({...user, [name]: value})
  }

  function handleSubmit(event: any) {
    event.preventDefault();
    if (!user.name || !user.surname) return;
    addUser(user);
    //console.log(user);
    setUser(initialState);
  }

  return (
    <form onSubmit={handleSubmit}>
      <h1>Name</h1>
      <input type="text" name="name" value={user.name}  onChange={handleChange}/>
      <h1>Surname</h1>
      <input type="text" name="surname" value={user.surname} onChange={handleChange}/>
      <h1>Age</h1>
      <input type="text" name="age" value={user.age} onChange={handleChange}/>
      <button>Add new User</button>
    </form>
  )
}

interface Props {
  users: {
    id: number;
    name: string;
    surname: string;
    age: number;
  }[],
  deleteUser(id: number) : any;
}

const AddTable: FC<Props> = ({users, deleteUser}) => {
  function handleDeleteUser(id: number) {
    let answer = window.confirm('Are you sure?')
    if (answer) {
      deleteUser(id)
    }
    console.log(users);
  }
  let user1: any[] = users.map((el: any) =>
    (<div className="row">
      <div key="id" className="col">{el.id}</div>
      <div key="name" className="col">{el.name}</div>
      <div key="surname" className="col">{el.surname}</div>
      <div key="age" className="col">{el.age}</div>
      <button value="delete" onClick={()=>handleDeleteUser(el.id)}>delete</button>
    </div>))
  return (
      <div>{user1}</div>
  )
}

const usersData = [
  { id: 1, name: 'Tania', surname: 'floppydiskette', age: 21 },
  { id: 2, name: 'Max', surname: 'maxfarseer', age: 23 },
]

const App: FC= () => {

  const [users, setUsers] = useState(usersData);
  function addUser(user: any){
    user.id = users.length+1;
    users.push(user);
    setUsers([...users]);
  }
  const deleteUser = (id: number)=> {
    setUsers(users.filter(el=> el.id!==id));
    console.log(users);
  }
  return(
    <div className="container">
      <h1>App with Hooks</h1>
      <div className="flex-row">
        <div className="flex-large">
          <h2>Add user</h2>
          <AddUserForm  addUser={addUser}/>
        </div>
        <div className="flex-large">
          <h2>View users</h2>
          <AddTable users={users} deleteUser={deleteUser} />
        </div>
      </div>
    </div>
  )
}

export default App;
