import React, { useState, FC, useCallback, useEffect, useReducer } from "react";
// import logo from './logo.svg';
import "./App.css";
import { setServers } from "dns";
// import { userInfo } from 'os';
// import { stringify } from 'querystring';


const AddUserForm: FC<{addUser(user: any): any}> = ({addUser}) => {
  const initialState = {
    id: 0,
    name: '',
    surname: '',
    age: 0
  }
  const [user, setUser] = useState(initialState);

  function handleChange(event: any) {

    const {name, value} = event.currentTarget;
    setUser({...user, [name]: value})
  }

  function handleSubmit(event: any) {
    event.preventDefault();
    if (!user.name || !user.surname) return;
    addUser(user);
    //console.log(user);
    setUser(initialState);
  }

  return (
    <form onSubmit={handleSubmit}>
      <h1>Name</h1>
      <input type="text" name="name" value={user.name}  onChange={handleChange}/>
      <h1>Surname</h1>
      <input type="text" name="surname" value={user.surname} onChange={handleChange}/>
      <h1>Age</h1>
      <input type="text" name="age" value={user.age} onChange={handleChange}/>
      <button>Add new User</button>
    </form>
  )
}

interface Props {
  users: {
    id: number;
    name: string;
    surname: string;
    age: number;
  }[],
  deleteUser(id: number) : any;
  editRow(el: any): any
}

const EditUserForm: FC<{editing: any, setEditing: any, currentUser: any, updateUser: any}> = ({editing, setEditing, currentUser, updateUser}) => {
  const [user, setUser] = useState(currentUser);
  useEffect(()=>{
    setUser(currentUser)
  }, [currentUser])
  function handleChange(event: any) {
    const {name, value} = event.target;
    setUser({...user, [name]:value})
  }
  function handleClick() {
    setEditing(false);
    updateUser(user)
  }
  return (
    <div>{currentUser.name}
      Name<input value={user.name} name="name" onChange={handleChange} type="text" />
      Surname<input value={user.surname} name="surname" onChange={handleChange} type="text" />
      Age<input value={user.age} name="age" onChange={handleChange} type="text" />
      <button onClick={handleClick}>Edit</button>
    </div>
  )
}

const AddTable: FC<Props> = ({users, deleteUser, editRow}) => {
  function handleDeleteUser(id: number) {
    let answer = window.confirm('Are you sure?')
    if (answer) {
      deleteUser(id)
    }
    console.log(users);
  }
  function handleEditUser(el: any) {
    editRow(el);
  }
  let user1: any[] = users.map((el: any) =>
    (<div className="row">
      <div key="id" className="col">{el.id}</div>
      <div key="name" className="col">{el.name}</div>
      <div key="surname" className="col">{el.surname}</div>
      <div key="age" className="col">{el.age}</div>
      <button onClick={()=>handleDeleteUser(el.id)}>delete</button>
      <button onClick={()=>handleEditUser(el)}>edit</button>
    </div>))
  return (
      <div>{user1}</div>
  )
}

const usersData = [
  { id: 1, name: 'Tania', surname: 'floppydiskette', age: 21 },
  { id: 2, name: 'Max', surname: 'maxfarseer', age: 23 },
]

const App: FC= () => {
  const [users, setUsers] = useState(usersData);
  const [editing, setEditing] = useState(false);
  const initialState = {
    id: 0,
    name: '',
    surname: '',
    age: 0
  }
  const [currentUser, setCurrentUser] = useState(initialState);

  function addUser(user: any){
    user.id = users.length+1;
    users.push(user);
    setUsers([...users]);
  }
  const deleteUser = (id: number)=> {
    setUsers(users.filter(el=> el.id!==id));
  }
  const editRow = (user: any) => {
    setEditing(true);
    setCurrentUser({id: user.id, name: user.name, surname: user.surname, age: user.age})
  }
  const updateUser = (currentUser: any)=> {
    setUsers(users.map(el => el.id===currentUser.id ? currentUser: el));
  }
  return(
    <div className="container">
      <h1>App with Hooks</h1>
      <div className="flex-row">
        {editing ? (<div className="flex-large">
          <h1>Edit User</h1>
          <EditUserForm   editing={editing}
                          setEditing={setEditing}
                          currentUser={currentUser} 
                          updateUser={updateUser}/>
        </div>) :
        (<div className="flex-large">
          <h2>Add user</h2>
          <AddUserForm  addUser={addUser}/>
        </div>)}
        <div className="flex-large">
          <h2>View users</h2>
          <AddTable users={users} deleteUser={deleteUser} editRow={editRow}/>
        </div>
      </div>
    </div>
  )
}

export default App;
