import React, { useState, FC, useCallback } from "react";
// import logo from './logo.svg';
import "./App.css";
// import { userInfo } from 'os';
// import { stringify } from 'querystring';

// function Handle() {
//   const [count, setCount] = useState(0);
//   return (
//     <div>
//       <button onClick={()=>setCount(count+1)}>+</button>
//       {count}
//       <button onClick={()=>setCount(count-1)}>-</button>
//     </div>
//   )
// }

const Counter: FC<{ count: number; setCount(c: number): void }> = ({ count, setCount }) => {
  const increment = useCallback(() => {
    setCount(count + 1);
  }, [count, setCount]);

  const decrement = useCallback(() => {
    setCount(count + 1);
  }, [count, setCount]);

  return (
    <>
      <button onClick={increment}>+</button>
      {count}
      <button onClick={decrement}>-</button>
    </>
  );
};

const CounterI: FC<{initialCount: number}>= ({initialCount}) => {
  const [count, setCount] = useState(initialCount);
  return (
    <>
      Счёт: {count}
      <button onClick={() => setCount(initialCount)}>Сбросить</button>
      <button onClick={() => setCount(prevCount => prevCount - 1)}>-</button>
      <button onClick={() => setCount(prevCount => prevCount + 1)}>+</button>
    </>
  );
}
const App: React.FunctionComponent = () => {
  const [count, setCount] = useState(0);
  let initialCount = 0;
  return (
    <div>
      <div>
        <Counter count={count} setCount={setCount} />
      </div>
      <div>
        <Counter count={count} setCount={setCount} />
      </div>
      <CounterI initialCount={initialCount} />
    </div>
  );
};

export default App;
