import React, { useState, FC, useCallback, useEffect, useReducer } from "react";
// import logo from './logo.svg';
import "./App.css";
import { setServers } from "dns";
// import { userInfo } from 'os';
// import { stringify } from 'querystring';


interface Props {
  users: {
    id: number;
    name: string;
    surname: string;
    age: number;
}[],
  addUser(user: any): any;
}
const AddUserForm: FC = (props: any) => {
  const initialState = {
    id: 0,
    name: '',
    surname: '',
    age: 0
  }
  const [user, setUser] = useState(initialState);
  function handleChange(event: any) {
    const {name, value} = event.CurrentTarget;
    setUser({...user, [name]: value})
  }
  function handleSubmit(event: any) {
    event.preventDefault();
    props.addUser(user);
    setUser(initialState);
  }
  return (
    <form >
      <h1>Name</h1>
      <input type="text" value={user.name}  onChange={handleChange}/>
      <h1>Surname</h1>
      <input type="text" value={user.surname} onChange={handleChange}/>
      <h1>Age</h1>
      <input type="text" value={user.age} onChange={handleChange}/>
      <button value="add" onClick={handleSubmit}>Add new User</button>
    </form>
  )
}
const AddTable: FC<Props> = ({users}) => {
  let user1: any[] = users.map((el: any) =>
    (<div className="row">
      <div className="col">{el.id}</div>
      <div className="col">{el.name}</div>
      <div className="col">{el.surname}</div>
      <div className="col">{el.age}</div>
    </div>))
  return (
      <div>{user1}</div>
  )
}


const App: FC= () => {
  const usersData = [
    { id: 1, name: 'Tania', surname: 'floppydiskette', age: 21 },
    { id: 2, name: 'Max', surname: 'maxfarseer', age: 23 },
  ]
  const [users, setUsers] = useState(usersData);
  function addUser(user: any){
    user.id = users.length+1;
    setUsers([...users,user])
  }
  return(
    <div className="container">
      <h1>CRUD App with Hooks</h1>
      <div className="flex-row">
        <div className="flex-large">
          <h2>Add user</h2>
          {/* добавили форму */}
          <AddUserForm />
        </div>
        <div className="flex-large">
          <h2>View users</h2>
          <AddTable users={usersData} addUser={addUser}/>
        </div>
      </div>
    </div>
  )
}

export default App;
